const burgerMenuIcon=document.querySelector("#burger_menu"),burgerMenuList=document.querySelector(".burger_menu"),burgerImg=document.querySelector(".burger"),burgerSpan=document.querySelector(".burger-span");burgerMenuIcon.addEventListener("click",()=>{burgerMenuList.classList.toggle("hide"),burgerImg.classList.toggle("margin_right")}),burgerSpan.addEventListener("click",()=>{burgerMenuList.classList.toggle("hide"),burgerImg.classList.toggle("margin_right")});const blogLink=document.querySelector("#blog_link"),addPostLink=document.querySelector("#addPost_link"),blog=document.querySelector("#blog"),addPost=document.querySelector("#addPost");function hideShow(e,t){e.classList.toggle("hide"),t.classList.toggle("hide")}addPostLink.addEventListener("click",()=>{addPost.classList.contains("hide")&&hideShow(blog,addPost)}),blogLink.addEventListener("click",()=>{blog.classList.contains("hide")&&hideShow(addPost,blog)});const overViewVector=document.querySelector("#overview_vector"),overViewActiveVector=document.querySelector("#overview_active_vector"),overViewList=document.querySelector(".overview_list");overViewVector.addEventListener("click",()=>{overViewActiveVector.classList.contains("hide")&&(overViewList.classList.remove("hide"),hideShow(overViewActiveVector,overViewVector))}),overViewActiveVector.addEventListener("click",()=>{overViewVector.classList.contains("hide")&&(overViewList.classList.add("hide"),hideShow(overViewActiveVector,overViewVector))});